const webhook = $('Set Client Config').first().json;
const sessionData = $('Get Session Data').first().json;
const dialogHistory = $('Get Dialog History').all().map(item => item.json);
const stages = webhook.CLIENT_CONFIG.stages;

return [{
  json: {
    webhook: webhook,
    sessionData: sessionData,
    dialogHistory: dialogHistory,
    stages: stages,
    CLIENT_CONFIG: webhook.CLIENT_CONFIG
  }
}];

// –û–ü–¢–ò–ú–ò–ó–ò–†–û–í–ê–ù–ù–ê–Ø –í–ï–†–°–ò–Ø - –∏—Å–ø–æ–ª—å–∑—É–µ—Ç stages –∏–∑ CLIENT_CONFIG
// –ù–ï —Ç—Ä–µ–±—É–µ—Ç –¥–æ–ø–æ–ª–Ω–∏—Ç–µ–ª—å–Ω—ã—Ö API –∑–∞–ø—Ä–æ—Å–æ–≤!

const CLIENT_CONFIG = items[0].json.CLIENT_CONFIG || {};
const startTime = Date.now();
const mergedData = items[0].json;
const requestData = mergedData.webhook;
const sessionId = requestData.body?.sessionId || requestData.sessionId || null;
const sessionData = mergedData.sessionData;
const dialogHistory = mergedData.dialogHistory;
const stages = mergedData.stages;

let leadScoring = {};
let bant = {};
let entities = {};
let customerNeeds = [];
let recommendations = [];
let emotionalTone = {};

try {
  leadScoring = sessionData.lead_scoring ? 
    (typeof sessionData.lead_scoring === 'string' ? 
      JSON.parse(sessionData.lead_scoring) : sessionData.lead_scoring) : {};

  if (sessionData.bant_qualification) {
    bant = typeof sessionData.bant_qualification === 'string' ? 
      JSON.parse(sessionData.bant_qualification) : sessionData.bant_qualification;
  }

  if (sessionData.extracted_entities) {
    entities = typeof sessionData.extracted_entities === 'string' ? 
      JSON.parse(sessionData.extracted_entities) : sessionData.extracted_entities;
  }

  customerNeeds = sessionData.customer_needs ? 
    (typeof sessionData.customer_needs === 'string' ? 
      JSON.parse(sessionData.customer_needs) : sessionData.customer_needs) : [];

  recommendations = sessionData.recommendations ? 
    (typeof sessionData.recommendations === 'string' ? 
      JSON.parse(sessionData.recommendations) : sessionData.recommendations) : [];

  emotionalTone = sessionData.emotional_tone ?
    (typeof sessionData.emotional_tone === 'string' ?
      JSON.parse(sessionData.emotional_tone) : sessionData.emotional_tone) : {};
} catch (e) {
  console.error('Parse error:', e);
}

const validateEmail = (email) => {
  if (!email) return { valid: false, value: null };
  const regex = /^[^\s@]+@[^\s@]+\.[^\s@]+$/;
  return { valid: regex.test(email), value: email };
};

const validatePhone = (phone) => {
  if (!phone) return { valid: false, value: null };
  const cleaned = phone.replace(/[^\d+]/g, '');
  return { valid: cleaned.length >= 10, value: cleaned };
};

const emailData = entities?.contacts?.email?.value ? 
  validateEmail(entities.contacts.email.value) : validateEmail(sessionData.email);

const phoneData = entities?.contacts?.phone?.value ? 
  validatePhone(entities.contacts.phone.value) : validatePhone(sessionData.phone);

const telegram = entities?.contacts?.telegram?.value || sessionData.telegram || null;
const whatsapp = entities?.contacts?.whatsapp?.value || sessionData.whatsapp || null;

const firstName = entities?.person?.firstName || sessionData.first_name || '';
const lastName = entities?.person?.lastName || sessionData.last_name || '';
const fullName = entities?.person?.fullName || sessionData.full_name || 
  `${firstName} ${lastName}`.trim() || '–ö–ª–∏–µ–Ω—Ç –∏–∑ —á–∞—Ç–∞';
const companyName = entities?.organization?.companyName || sessionData.company || '';
const position = entities?.organization?.position || sessionData.position || '';
const location = entities?.location?.city || sessionData.location || '';

const leadScore = leadScoring?.score || 0;
const leadTemp = leadScoring?.temperature || 'cold';
const bantTotal = bant?.totalScore || 0;
const bantBudget = bant?.budget || {};
const bantNeed = bant?.need || {};
const bantAuthority = bant?.authority || {};
const bantTimeline = bant?.timeline || {};

// –£–º–Ω–æ–µ –æ–ø—Ä–µ–¥–µ–ª–µ–Ω–∏–µ —Å—Ç–∞–¥–∏–∏
function determineStage(leadTemp, stages, config) {
  // 1. –ü—Ä–æ–≤–µ—Ä—è–µ–º custom mapping
  const customStageId = config.stageMapping[leadTemp];
  if (customStageId) {
    const found = stages.find(s => s.id === customStageId);
    if (found) {
      return { stageId: found.id, stageName: found.name, method: 'custom' };
    }
  }

  // 2. –ü–æ–∏—Å–∫ –ø–æ keywords –≤ stages
  for (const stage of stages) {
    if (stage.keywords && Array.isArray(stage.keywords)) {
      for (const keyword of stage.keywords) {
        if (keyword.toLowerCase().includes(leadTemp.toLowerCase())) {
          return { stageId: stage.id, stageName: stage.name, method: 'keyword' };
        }
      }
    }
  }

  // 3. Fallback - –±–µ—Ä–µ–º –ø–æ –ø–æ–∑–∏—Ü–∏–∏
  const activeStages = stages.filter(s => s.type !== 1 && !s.isSuccess && !s.isClosed)
    .sort((a, b) => (a.sort || 0) - (b.sort || 0));

  let fallbackStage;
  if (leadTemp === 'hot') {
    fallbackStage = activeStages[Math.min(Math.floor(activeStages.length * 0.6), activeStages.length - 1)];
  } else if (leadTemp === 'warm') {
    fallbackStage = activeStages[Math.min(Math.floor(activeStages.length * 0.4), activeStages.length - 1)];
  } else {
    fallbackStage = activeStages[0];
  }

  return { stageId: fallbackStage.id, stageName: fallbackStage.name, method: 'fallback' };
}

const stageResult = determineStage(leadTemp, stages, CLIENT_CONFIG);

let opportunityAmount = 0;
if (bantBudget?.value && bantBudget.value > 0) {
  opportunityAmount = bantBudget.value;
} else if (bantBudget?.range?.min && bantBudget.range.min > 0) {
  opportunityAmount = bantBudget.range.min;
}

const customFieldsValues = [];

if (phoneData.valid) {
  customFieldsValues.push({
    field_id: CLIENT_CONFIG.customFields.phone,
    values: [{
      value: phoneData.value,
      enum_id: CLIENT_CONFIG.phoneEnums.WORK
    }]
  });
}

if (emailData.valid) {
  customFieldsValues.push({
    field_id: CLIENT_CONFIG.customFields.email,
    values: [{
      value: emailData.value,
      enum_id: CLIENT_CONFIG.emailEnums.WORK
    }]
  });
}

if (position) {
  customFieldsValues.push({
    field_id: CLIENT_CONFIG.customFields.position,
    values: [{
      value: position
    }]
  });
}

const closeDays = CLIENT_CONFIG.closeDays[leadTemp] || 60;
const closeDate = new Date();
closeDate.setDate(closeDate.getDate() + closeDays);
const closeDateUnix = Math.floor(closeDate.getTime() / 1000);

const utmData = {
  source: sessionData.utm_source || 'direct',
  medium: sessionData.utm_medium || 'none',
  campaign: sessionData.utm_campaign || 'organic'
};

const aiCommentInput = {
  leadScore: leadScore,
  leadTemperature: leadTemp,
  bantTotal: bantTotal,
  bantQualified: bant?.qualified || false,
  bant: {
    budget: {
      score: bantBudget?.score || 0,
      description: bantBudget?.description || '–ù–µ —É–∫–∞–∑–∞–Ω',
      value: bantBudget?.value || null,
      mentioned: bantBudget?.mentioned || false
    },
    authority: {
      score: bantAuthority?.score || 0,
      role: bantAuthority?.role || 'unknown',
      level: bantAuthority?.level || 'unknown',
      description: bantAuthority?.description || '–ù–µ –æ–ø—Ä–µ–¥–µ–ª–µ–Ω–æ'
    },
    need: {
      score: bantNeed?.score || 0,
      severity: bantNeed?.severity || 'unknown',
      painPoints: bantNeed?.painPoints || [],
      description: bantNeed?.description || '–ù–µ –≤—ã—è–≤–ª–µ–Ω–æ'
    },
    timeline: {
      score: bantTimeline?.score || 0,
      urgency: bantTimeline?.urgency || 'unknown',
      deadline: bantTimeline?.deadline || null,
      description: bantTimeline?.description || '–ù–µ —É–∫–∞–∑–∞–Ω–æ'
    }
  },
  customerProfile: {
  name: fullName,
  company: companyName,
  position: position || '',     // ‚Üê –ø—É—Å—Ç–∞—è —Å—Ç—Ä–æ–∫–∞ –µ—Å–ª–∏ null
  location: location || '',
  email: emailData.value || '', // ‚Üê –ø—É—Å—Ç–∞—è —Å—Ç—Ä–æ–∫–∞ –µ—Å–ª–∏ null
  phone: phoneData.value || ''  // ‚Üê –ø—É—Å—Ç–∞—è —Å—Ç—Ä–æ–∫–∞ –µ—Å–ª–∏ null
},
  customerNeeds: customerNeeds,
  painPoints: bantNeed?.painPoints || [],
  emotionalTone: {
    overall: emotionalTone?.overall || 'neutral',
    satisfaction: emotionalTone?.satisfaction || 50,
    description: emotionalTone?.description || ''
  },
  recommendations: recommendations,
  productsInterested: entities?.products || [],
  currentSolution: entities?.currentSolution || {},
  competitors: entities?.competitors || [],
  dialogHistory: dialogHistory.map(msg => ({
    role: msg.message_type,
    content: msg.content?.substring(0, 200) || ''
  })),
  messageCount: sessionData.message_count || dialogHistory.length,
  platform: sessionData.platform || 'webchat',
  utmData: utmData,
  industry: CLIENT_CONFIG.industry,
  stageName: stageResult.stageName,
  opportunityAmount: opportunityAmount,
  currency: CLIENT_CONFIG.defaultCurrency,
  priceList: CLIENT_CONFIG.priceList
};

return [{
  json: {
    clientName: CLIENT_CONFIG.clientName,
    sessionId: sessionId,
    leadScore: leadScore,
    leadTemperature: leadTemp,
    bantScore: bantTotal,
    bantQualified: bant?.qualified || false,
    bantLevel: bant?.qualificationLevel || 'cold',
    stageId: stageResult.stageId,
    stageName: stageResult.stageName,
    pipelineId: CLIENT_CONFIG.pipelineId,
    pipelineName: CLIENT_CONFIG.pipelineName,
    isHotLead: leadScore >= 70,
    responsibleUserId: requestData.assignedManager || CLIENT_CONFIG.defaultManagerId,
    subdomain: CLIENT_CONFIG.subdomain,
    sendTelegram: CLIENT_CONFIG.sendTelegramForHot && leadScore >= 70,
    telegramChatId: CLIENT_CONFIG.telegramChatId,
    contact: {
      name: fullName,
      first_name: firstName,
      last_name: lastName,
      custom_fields_values: customFieldsValues,
      responsible_user_id: requestData.assignedManager || CLIENT_CONFIG.defaultManagerId
    },
    lead: {
      name: `ü§ñ ${fullName} | Score: ${leadScore}${companyName ? ' | ' + companyName : ''}`,
      pipeline_id: CLIENT_CONFIG.pipelineId,
      status_id: stageResult.stageId,
      price: opportunityAmount,
      responsible_user_id: requestData.assignedManager || CLIENT_CONFIG.defaultManagerId,
      created_by: requestData.assignedManager || CLIENT_CONFIG.defaultManagerId,
      updated_by: requestData.assignedManager || CLIENT_CONFIG.defaultManagerId,
      closed_at: closeDateUnix
    },
    searchData: (() => {
  const data = {};
  if (phoneData.valid && phoneData.value) {
    data.phone = phoneData.value;
  }
  if (emailData.valid && emailData.value) {
    data.email = emailData.value;
  }
  return data;
})(),
    validation: {
      hasEmail: emailData.valid,
      hasPhone: phoneData.valid,
      hasName: !!fullName,
      isValid: emailData.valid || phoneData.valid,
      missingFields: [
        !emailData.valid && !phoneData.valid ? 'contact' : null,
        !fullName ? 'name' : null
      ].filter(Boolean)
    },
    utmData: utmData,
    aiCommentInput: aiCommentInput,
    startTime: startTime,
    CLIENT_CONFIG: CLIENT_CONFIG
  }
}];

