// –ê–î–ê–ü–¢–ò–†–û–í–ê–ù–ù–ê–Ø –í–ï–†–°–ò–Ø –î–õ–Ø amoCRM (–∫–∞–∫ –≤ Bitrix24)
const formattedData = $('Format Data (Optimized)').first().json;
const agentResponse = $('Generate AI Comment').first().json;

// üîç –ò–ó–í–õ–ï–ß–ï–ù–ò–ï AI –ö–û–ú–ú–ï–ù–¢–ê–†–ò–Ø (–ø–æ–¥–¥–µ—Ä–∂–∫–∞ –≤—Å–µ—Ö —Ñ–æ—Ä–º–∞—Ç–æ–≤)
let aiComment = '';
if (agentResponse.output) {
  aiComment = agentResponse.output;
} else if (agentResponse.text) {
  aiComment = agentResponse.text;
} else if (agentResponse.response) {
  aiComment = agentResponse.response;
} else {
  aiComment = JSON.stringify(agentResponse);
}

console.log('AI Comment extracted, length:', aiComment.length);

// üßπ –ú–ê–ö–°–ò–ú–ê–õ–¨–ù–ê–Ø –û–ß–ò–°–¢–ö–ê –¥–ª—è amoCRM API (–° –°–û–•–†–ê–ù–ï–ù–ò–ï–ú –°–¢–†–£–ö–¢–£–†–´)
aiComment = aiComment
  // –£–¥–∞–ª–∏—Ç—å —Å–ª—É–∂–µ–±–Ω—ã–µ –∑–∞–≥–æ–ª–æ–≤–∫–∏ –æ—Ç AI
  .replace(/###?\s*–û–ø—Ç–∏–º–∞–ª—å–Ω—ã–π –ö–æ–º–º–µ–Ω—Ç–∞—Ä–∏–π –¥–ª—è CRM[\s\S]*?(?=\n\*\*)/i, '')
  .replace(/–û–ü–¢–ò–ú–ê–õ–¨–ù–´–ô –ö–û–ú–ú–ï–ù–¢–ê–†–ò–ô –î–õ–Ø CRM/gi, '')

  // –£–¥–∞–ª–∏—Ç—å code blocks
  .replace(/```[\s\S]*?```/g, '')
  .replace(/```/g, '')
  .replace(/`{1,2}([^`]+)`{1,2}/g, '$1')

  // –£–î–ê–õ–ò–¢–¨ –í–°–ï –≠–ú–û–î–ó–ò –ò –°–ü–ï–¶–°–ò–ú–í–û–õ–´
  .replace(/[\u{1F300}-\u{1F9FF}]/gu, '')
  .replace(/[\u{2600}-\u{26FF}]/gu, '')
  .replace(/[\u{2700}-\u{27BF}]/gu, '')
  .replace(/[\u{1F600}-\u{1F64F}]/gu, '')
  .replace(/[\u{1F680}-\u{1F6FF}]/gu, '')
  .replace(/[\u{1F1E0}-\u{1F1FF}]/gu, '')
  .replace(/[üéØüî•üìäüí°‚ö°üö®‚úÖ‚ùå‚è≥üí∞üìåüå°Ô∏è‚ùÑÔ∏èüë§üí¨üè¢üìß‚òéÔ∏èüìçüéÅüîç‚ö†Ô∏èüìùüè≠üåêü§ùüõ°Ô∏èüöÄ‚ú®üßë‚Äçüíªü§ñüíª‚≠ê‚ô¶Ô∏è‚óÜ‚óè]/g, '')
  .replace(/[‚óÜ‚óè‚ô¶‚óá‚óã‚ñ†‚ñ°‚ñ™‚ñ´]/g, '')

  // –£–î–ê–õ–ò–¢–¨ –î–£–ë–õ–ò–†–£–Æ–©–ò–ï–°–Ø –ë–õ–û–ö–ò
  .replace(/IMPORTANT NOTES[\s\S]*?(?=CONVERSATION SUMMARY|METADATA|$)/i, '')
  .replace(/CONVERSATION SUMMARY[\s\S]*?(?=METADATA|$)/i, '')

  // –£–¥–∞–ª–∏—Ç—å markdown
  .replace(/^#+\s*/gm, '')
  .replace(/\*\*/g, '')
  .replace(/---/g, '')
  .replace(/===/g, '')

  // –û—á–∏—Å—Ç–∏—Ç—å —ç–∫—Ä–∞–Ω–∏—Ä–æ–≤–∞–Ω–Ω—ã–µ —Å–∏–º–≤–æ–ª—ã
  .replace(/\\n/g, '\n')
  .replace(/\\u[0-9a-fA-F]{4}/g, '')

  // –û—á–∏—Å—Ç–∏—Ç—å –¢–û–õ–¨–ö–û –ª–∏—à–Ω–∏–µ –ø–µ—Ä–µ–Ω–æ—Å—ã
  .replace(/\n{3,}/g, '\n\n')

  // –û—á–∏—Å—Ç–∏—Ç—å –ª–∏—à–Ω–∏–µ –ø—Ä–æ–±–µ–ª—ã
  .replace(/ +/g, ' ')
  .replace(/\n /g, '\n')
  .replace(/ \n/g, '\n')

  .trim();

