const staticData = $getWorkflowStaticData('global');
if (!staticData.rateLimits) { staticData.rateLimits = {}; }
for (const ip in staticData.rateLimits) {
  if (now - staticData.rateLimits[ip].firstRequest > RATE_LIMIT_WINDOW_MS) {
    delete staticData.rateLimits[ip];
