// ============================================
// –ó–ê–©–ò–¢–ê: API KEY + RATE LIMITING
// ============================================

const API_KEY = '24fs-$r4d-defd-77ds-7eds';
const RATE_LIMIT_WINDOW_MS = 60000;
const RATE_LIMIT_MAX_REQUESTS = 30;

const inputData = $input.first().json;
const body = inputData.body || {};
const headers = inputData.headers || {};
const query = inputData.query || {};
const clientIP = headers['x-real-ip'] || headers['x-forwarded-for'] || headers['cf-connecting-ip'] || 'unknown';
const now = Date.now();

// RATE LIMITING
const staticData = $getWorkflowStaticData('global');
if (!staticData.rateLimits) { staticData.rateLimits = {}; }
for (const ip in staticData.rateLimits) {
  if (now - staticData.rateLimits[ip].firstRequest > RATE_LIMIT_WINDOW_MS) {
    delete staticData.rateLimits[ip];
  }
}
if (!staticData.rateLimits[clientIP]) {
  staticData.rateLimits[clientIP] = { count: 1, firstRequest: now };
} else {
  staticData.rateLimits[clientIP].count++;
  if (staticData.rateLimits[clientIP].count > RATE_LIMIT_MAX_REQUESTS) {
    return [{ json: { ...inputData, authorized: false, error: { success: false, error: 'Too many requests', status: 429 } } }];
  }
}

// API KEY CHECK
const providedKey = headers['x-api-key'] || body.apiKey || query.apiKey || '';
if (!providedKey || providedKey !== API_KEY) {
  return [{ json: { ...inputData, authorized: false, error: { success: false, error: 'Unauthorized', status: 401 } } }];
}

return [{ json: { ...inputData, authorized: true } }];

// –ü–∞—Ä—Å–∏–º –ø–∞—Ä–∞–º–µ—Ç—Ä—ã –∑–∞–ø—Ä–æ—Å–∞
const webhookData = items[0].json;
const body = webhookData.body || webhookData;

const sessionId = body.sessionId || body.session_id || null;
const email = body.email || null;
const format = body.format || 'json';

// –í–∞–ª–∏–¥–∞—Ü–∏—è - –Ω—É–∂–µ–Ω —Ö–æ—Ç—è –±—ã –æ–¥–∏–Ω –ø–∞—Ä–∞–º–µ—Ç—Ä –ø–æ–∏—Å–∫–∞
if (!sessionId && !email) {
    throw new Error('Please provide Session ID or Email to search');
}

return [{
    json: {
        session_id: sessionId,
        email: email ? email.toLowerCase().trim() : null,
        format: format,
        search_by: sessionId ? 'session_id' : 'email',
        requested_at: new Date().toISOString()
    }
}];

// –°–æ–±–∏—Ä–∞–µ–º –Ω–∞–π–¥–µ–Ω–Ω—ã–µ session_id
const requestData = $('Parse Request').first().json;
const emailResults = $('Find Sessions by Email').all().map(i => i.json).filter(i => i && i.session_id);

if (emailResults.length === 0) {
    // Email –Ω–µ –Ω–∞–π–¥–µ–Ω –Ω–∏ –≤ –æ–¥–Ω–æ–π —Ç–∞–±–ª–∏—Ü–µ
    return [{
        json: {
            session_ids: [],
            email: requestData.email,
            found: false,
            format: requestData.format,
            message: 'No sessions found for this email'
        }
    }];
}

// –°–æ–±–∏—Ä–∞–µ–º —É–Ω–∏–∫–∞–ª—å–Ω—ã–µ session_id
const sessionIds = [...new Set(emailResults.map(r => r.session_id))];

return [{
    json: {
        session_ids: sessionIds,
        email: requestData.email,
        found: true,
        format: requestData.format,
        sources: emailResults.map(r => ({ session_id: r.session_id, source: r.source }))
    }
}];

// –ü–æ–¥–≥–æ—Ç–∞–≤–ª–∏–≤–∞–µ–º –¥–∞–Ω–Ω—ã–µ –¥–ª—è –ø—Ä—è–º–æ–≥–æ –ø–æ–∏—Å–∫–∞ –ø–æ session_id
const requestData = $('Parse Request').first().json;

return [{
    json: {
        session_ids: [requestData.session_id],
        session_id: requestData.session_id,
        email: null,
        found: true,
        format: requestData.format
    }
}];

// –§–æ—Ä–º–∏—Ä—É–µ–º SQL IN clause –¥–ª—è session_ids
const data = items[0].json;
const sessionIds = data.session_ids || [];

// –§–æ—Ä–º–∏—Ä—É–µ–º —Å—Ç—Ä–æ–∫—É –¥–ª—è SQL IN
const sessionIdsSQL = sessionIds.map(id => `'${id}'`).join(', ');

return [{
    json: {
        ...data,
        session_ids_sql: sessionIdsSQL
    }
}];

// ============================================
// ‚öôÔ∏è CONFIGURATION - MODIFY FOR YOUR COMPANY
// ============================================
const CONFIG = {
    // Company role under GDPR: 'Data Controller' or 'Data Processor'
    // Example: 'Data Controller'
    companyRole: 'Data Processor',

    // DPO (Data Protection Officer) email address
    // Use null for auto-generation from domain (dpo@yourdomain.com)
    // Example: 'privacy@yourcompany.com' or 'dpo@yourcompany.com' or null
    dpoEmail: null,

    // Legal basis for data processing (GDPR Article 6)
    // Example: 'Consent (GDPR Article 6(1)(a))'
    // Example: 'Contract (GDPR Article 6(1)(b))'
    // Example: 'Legitimate Interest (GDPR Article 6(1)(f))'
    legalBasis: 'Consent (GDPR Article 6(1)(a))',

    // Purposes of data processing (array of strings)
    // Example: ['marketing', 'analytics', 'customer_support']
    processingPurposes: [
        'lead_qualification',
        'customer_support'
    ],

    // Data retention periods (displayed in report)
    // Modify values according to your data retention policy
    retentionPolicy: {
        chatHistory: '365 days',
        consentRecords: '5 years (legal requirement)',
        auditLogs: '7 years (accountability requirement)'
    },

    // Data protection measures (displayed in report)
    // Add or remove items according to your security measures
    dataProtectionMeasures: [
        'Data encrypted in transit (TLS 1.3)',
        'Data encrypted at rest (AES-256)',
        'Access controls implemented',
        'Audit logging enabled',
        'Regular security assessments'
    ],

    // Data subject rights (displayed in report)
    // Standard GDPR rights - usually no changes needed
    dataSubjectRights: [
        'Right of Access (Article 15)',
        'Right to Rectification (Article 16)',
        'Right to Erasure (Article 17)',
        'Right to Restriction (Article 18)',
        'Right to Data Portability (Article 20)',
        'Right to Object (Article 21)'
    ]
};
// ============================================
// ‚õî DO NOT MODIFY CODE BELOW THIS LINE
// ============================================

// –§–æ—Ä–º–∏—Ä—É–µ–º –ø–æ–ª–Ω—ã–π –æ—Ç—á—ë—Ç GDPR Audit
const searchParams = $('Prepare SQL Query').first().json;

// –ü–æ–ª—É—á–∞–µ–º –¥–∞–Ω–Ω—ã–µ –∏–∑ –≤—Å–µ—Ö –Ω–æ–¥
let auditLogs = [];
let consentRecords = [];
let userEmails = [];

try {
    const auditLogsRaw = $('Get Audit Logs').all();
    auditLogs = auditLogsRaw
        .map(i => i.json)
        .filter(item => item && item.id);
} catch (e) {
    auditLogs = [];
}

try {
    const consentRecordsRaw = $('Get Consent Records').all();
    consentRecords = consentRecordsRaw
        .map(i => i.json)
        .filter(item => item && item.session_id);
} catch (e) {
    consentRecords = [];
}

try {
    const userEmailsRaw = $('Get User Emails').all();
    userEmails = userEmailsRaw
        .map(i => i.json)
        .filter(item => item && item.email);
} catch (e) {
    userEmails = [];
}

// –°–æ–∑–¥–∞—ë–º –º–∞–ø–ø–∏–Ω–≥ session_id -> email/name
const sessionEmailMap = {};
userEmails.forEach(u => {
    if (!sessionEmailMap[u.session_id]) {
        sessionEmailMap[u.session_id] = {
            email: u.email,
            name: u.name,
            source: u.source
        };
    }
});

// –û–ø—Ä–µ–¥–µ–ª—è–µ–º –æ—Å–Ω–æ–≤–Ω–æ–π email (–ø–µ—Ä–≤—ã–π –Ω–∞–π–¥–µ–Ω–Ω—ã–π –∏–ª–∏ –∏–∑ –ø–∞—Ä–∞–º–µ—Ç—Ä–æ–≤ –ø–æ–∏—Å–∫–∞)
const primaryEmail = searchParams.email || (userEmails[0]?.email) || null;
const primaryName = userEmails[0]?.name || null;

// –ï—Å–ª–∏ –Ω–µ—Ç –¥–∞–Ω–Ω—ã—Ö
if (auditLogs.length === 0 && consentRecords.length === 0) {
    return [{
        json: {
            report: {
                reportMetadata: {
                    title: 'GDPR Article 30 - Record of Processing Activities',
                    subtitle: 'Data Subject Audit Report',
                    generatedAt: new Date().toISOString(),
                    generatedBy: 'GDPR Compliance System',
                    reportFormat: searchParams.format,
                    queryParameters: {
                        sessionIds: searchParams.session_ids,
                        email: searchParams.email
                    },
                    noDataFound: true
                },
                dataSubjectSummary: {
                    sessionIds: searchParams.session_ids,
                    email: searchParams.email,
                    totalAuditRecords: 0,
                    totalConsentRecords: 0,
                    dataCategories: [],
                    actionBreakdown: {}
                },
                processingActivities: [],
                consentHistory: [],
                message: 'No audit records found for the specified criteria'
            },
            format: searchParams.format,
            session_ids: searchParams.session_ids,
            email: searchParams.email,
            totalRecords: 0
        }
    }];
}

// –û–ø—Ä–µ–¥–µ–ª—è–µ–º –∫–æ–Ω—Ç—Ä–æ–ª–ª–µ—Ä–∞ –∏ –¥–æ–º–µ–Ω
const primaryDomain = auditLogs[0]?.domain || consentRecords[0]?.domain || 'Unknown';
const controller = auditLogs[0]?.controller || primaryDomain;
const cleanDomain = String(primaryDomain).replace(/^https?:\/\//, '').split('/')[0];

// DPO email - –∏–∑ –∫–æ–Ω—Ñ–∏–≥–∞ –∏–ª–∏ –∞–≤—Ç–æ–≥–µ–Ω–µ—Ä–∞—Ü–∏—è
const dpoEmail = CONFIG.dpoEmail || ('dpo@' + cleanDomain);

// –°—Ç–∞—Ç–∏—Å—Ç–∏–∫–∞ –ø–æ –¥–µ–π—Å—Ç–≤–∏—è–º
const actionStats = {};
auditLogs.forEach(log => {
    if (log.action) {
        actionStats[log.action] = (actionStats[log.action] || 0) + 1;
    }
});

// –£–Ω–∏–∫–∞–ª—å–Ω—ã–µ –∫–∞—Ç–µ–≥–æ—Ä–∏–∏ –¥–∞–Ω–Ω—ã—Ö
const allCategories = new Set();
auditLogs.forEach(log => {
    if (log.data_categories && Array.isArray(log.data_categories)) {
        log.data_categories.forEach(cat => allCategories.add(cat));
    }
});

// –£–Ω–∏–∫–∞–ª—å–Ω—ã–µ session_id –≤ —Ä–µ–∑—É–ª—å—Ç–∞—Ç–∞—Ö
const uniqueSessionIds = [...new Set(auditLogs.map(l => l.session_id))];

// –§–æ—Ä–º–∏—Ä—É–µ–º –æ—Ç—á—ë—Ç
const report = {
    reportMetadata: {
        title: 'GDPR Article 30 - Record of Processing Activities',
        subtitle: 'Data Subject Audit Report',
        generatedAt: new Date().toISOString(),
        generatedBy: 'GDPR Compliance System',
        reportFormat: searchParams.format,
        queryParameters: {
            sessionIds: searchParams.session_ids,
            email: primaryEmail,
            searchedBy: searchParams.email ? 'email' : 'session_id'
        }
    },

    controllerInformation: {
        name: controller,
        domain: primaryDomain,
        role: CONFIG.companyRole,
        contactDpo: dpoEmail
    },

    dataSubjectInformation: {
        email: primaryEmail,
        name: primaryName,
        sessionIds: uniqueSessionIds,
        emailSources: userEmails.map(u => ({
            sessionId: u.session_id,
            email: u.email,
            name: u.name,
            source: u.source
        }))
    },

    dataSubjectSummary: {
        sessionIds: uniqueSessionIds,
        email: primaryEmail,
        name: primaryName,
        totalAuditRecords: auditLogs.length,
        totalConsentRecords: consentRecords.length,
        firstActivity: auditLogs[0]?.created_at || null,
        lastActivity: auditLogs[auditLogs.length - 1]?.created_at || null,
        dataCategories: Array.from(allCategories),
        actionBreakdown: actionStats
    },

    legalBasis: {
        primaryBasis: CONFIG.legalBasis,
        purposes: CONFIG.processingPurposes,
        retentionPolicy: CONFIG.retentionPolicy
    },

    consentHistory: consentRecords.map(consent => {
        // –ü–æ–¥—Ç—è–≥–∏–≤–∞–µ–º email –∏–∑ sessionEmailMap –µ—Å–ª–∏ –Ω–µ—Ç –≤ consent
        const emailInfo = sessionEmailMap[consent.session_id] || {};
        return {
            sessionId: consent.session_id,
            email: consent.user_email || emailInfo.email || null,
            name: emailInfo.name || null,
            consentGiven: consent.consent_given,
            consentType: consent.consent_type,
            privacyPolicyVersion: consent.privacy_policy_version,
            isActive: consent.is_active,
            createdAt: consent.created_at,
            expiresAt: consent.expires_at,
            revokedAt: consent.revoked_at,
            revokeReason: consent.revoke_reason
        };
    }),

    processingActivities: auditLogs.map(log => {
        let parsedDetails = {};
        try {
            parsedDetails = typeof log.details === 'string' ? JSON.parse(log.details) : (log.details || {});
        } catch (e) {
            parsedDetails = { raw: log.details };
        }

        // –ü–æ–¥—Ç—è–≥–∏–≤–∞–µ–º email –¥–ª—è —ç—Ç–æ–π —Å–µ—Å—Å–∏–∏
        const emailInfo = sessionEmailMap[log.session_id] || {};

        return {
            id: log.id,
            sessionId: log.session_id,
            email: emailInfo.email || null,
            name: emailInfo.name || null,
            timestamp: log.created_at,
            action: log.action,
            actionDescription: getActionDescription(log.action),
            gdprArticle: getGdprArticle(log.action),
            purpose: log.purpose,
            legalBasis: log.legal_basis,
            controller: log.controller,
            dataCategories: log.data_categories,
            details: parsedDetails,
            technicalInfo: {
                ipAddress: log.ip_address,
                userAgent: log.user_agent,
                domain: log.domain
            },
            privacyPolicyVersion: log.privacy_policy_version
        };
    }),

    complianceStatement: {
        declaration: 'This report is generated in accordance with GDPR Article 30 requirements for maintaining records of processing activities.',
        dataProtectionMeasures: CONFIG.dataProtectionMeasures,
        dataSubjectRights: CONFIG.dataSubjectRights
    }
};

function getActionDescription(action) {
    const descriptions = {
        'consent_given': 'Data subject provided consent for data processing',
        'consent_declined': 'Data subject declined consent for data processing',
        'consent_revoked': 'Data subject withdrew previously given consent',
        'data_access': 'Data subject requested access to their personal data',
        'data_export': 'Data subject requested export of their personal data',
        'data_erasure': 'Data subject requested deletion of their personal data',
        'personal_data_collected': 'Personal data was collected from data subject'
    };
    return descriptions[action] || action;
}

function getGdprArticle(action) {
    const articles = {
        'consent_given': 'Article 7 - Conditions for consent',
        'consent_declined': 'Article 7 - Conditions for consent',
        'consent_revoked': 'Article 7(3) - Right to withdraw consent',
        'data_access': 'Article 15 - Right of access',
        'data_export': 'Article 20 - Right to data portability',
        'data_erasure': 'Article 17 - Right to erasure',
        'personal_data_collected': 'Article 13/14 - Information to be provided'
    };
    return articles[action] || 'Article 30 - Records of processing activities';
}

return [{
    json: {
        report: report,
        format: searchParams.format,
        session_ids: searchParams.session_ids,
        email: primaryEmail,
        totalRecords: auditLogs.length
    }
}];

// –ì–µ–Ω–µ—Ä–∏—Ä—É–µ–º CSV
const reportData = items[0].json;
const report = reportData.report;
const activities = report.processingActivities || [];
const dataSubject = report.dataSubjectInformation || {};
const sessionIds = dataSubject.sessionIds || report.reportMetadata?.queryParameters?.sessionIds || [];
const primaryEmail = dataSubject.email || report.dataSubjectSummary?.email || '';
const primaryName = dataSubject.name || report.dataSubjectSummary?.name || '';

if (activities.length === 0) {
    return [{
        json: {
            success: true,
            format: 'csv',
            filename: `gdpr_audit_report_${new Date().toISOString().split('T')[0]}.csv`,
            content: 'No data found for the specified criteria',
            contentType: 'text/csv; charset=utf-8',
            totalRecords: 0
        }
    }];
}

// –§—É–Ω–∫—Ü–∏—è —ç–∫—Ä–∞–Ω–∏—Ä–æ–≤–∞–Ω–∏—è CSV
function escapeCSV(value) {
    if (value === null || value === undefined) return '';
    const str = String(value);
    if (str.includes(',') || str.includes('"') || str.includes('\n')) {
        return '"' + str.replace(/"/g, '""') + '"';
    }
    return str;
}

// –ú–µ—Ç–∞–¥–∞–Ω–Ω—ã–µ –æ—Ç—á—ë—Ç–∞ –≤ –Ω–∞—á–∞–ª–µ CSV
const metadata = [
    `# GDPR Article 30 Audit Report`,
    `# Generated: ${report.reportMetadata?.generatedAt || new Date().toISOString()}`,
    `# Data Subject Email: ${primaryEmail || 'N/A'}`,
    `# Data Subject Name: ${primaryName || 'N/A'}`,
    `# Session IDs: ${sessionIds.join('; ') || 'N/A'}`,
    `# Controller: ${report.controllerInformation?.name || 'N/A'}`,
    `# Total Records: ${activities.length}`,
    ``
];

const headers = [
    'ID', 'Session ID', 'Email', 'Name', 'Timestamp', 'Action', 'Action Description', 'GDPR Article',
    'Purpose', 'Legal Basis', 'Controller', 'Data Categories', 'IP Address', 'Domain', 'Details'
];

const rows = activities.map(act => [
    act.id,
    act.sessionId,
    act.email || primaryEmail,
    act.name || primaryName,
    act.timestamp,
    act.action,
    act.actionDescription,
    act.gdprArticle,
    act.purpose,
    act.legalBasis,
    act.controller,
    Array.isArray(act.dataCategories) ? act.dataCategories.join('; ') : '',
    act.technicalInfo?.ipAddress || '',
    act.technicalInfo?.domain || '',
    JSON.stringify(act.details || {})
]);

// BOM –¥–ª—è UTF-8 (—á—Ç–æ–±—ã Excel –ø—Ä–∞–≤–∏–ª—å–Ω–æ —á–∏—Ç–∞–ª –∫–∏—Ä–∏–ª–ª–∏—Ü—É)
const BOM = '\uFEFF';

const csvContent = BOM + [
    ...metadata,
    headers.map(escapeCSV).join(','),
    ...rows.map(row => row.map(escapeCSV).join(','))
].join('\n');

return [{
    json: {
        success: true,
        format: 'csv',
        filename: `gdpr_audit_report_${new Date().toISOString().split('T')[0]}.csv`,
        content: csvContent,
        contentType: 'text/csv; charset=utf-8',
        totalRecords: activities.length
    }
}];

// –í–æ–∑–≤—Ä–∞—â–∞–µ–º JSON –æ—Ç—á—ë—Ç
const reportData = items[0].json;
const report = reportData.report;
const dataSubject = report.dataSubjectInformation || {};
const sessionIds = dataSubject.sessionIds || report.reportMetadata?.queryParameters?.sessionIds || [];

return [{
    json: {
        success: true,
        format: 'json',
        filename: `gdpr_audit_report_${new Date().toISOString().split('T')[0]}.json`,
        contentType: 'application/json',
        totalRecords: reportData.totalRecords || 0,
        summary: {
            email: dataSubject.email || null,
            name: dataSubject.name || null,
            sessionIds: sessionIds,
            totalActivities: report.dataSubjectSummary?.totalAuditRecords || 0,
            totalConsents: report.dataSubjectSummary?.totalConsentRecords || 0
        },
        data: report
    }
}];

// Email –Ω–µ –Ω–∞–π–¥–µ–Ω - –≤–æ–∑–≤—Ä–∞—â–∞–µ–º –ø—É—Å—Ç–æ–π —Ä–µ–∑—É–ª—å—Ç–∞—Ç
const data = items[0].json;

return [{
    json: {
        success: true,
        format: data.format || 'json',
        data: {
            reportMetadata: {
                title: 'GDPR Article 30 - Record of Processing Activities',
                generatedAt: new Date().toISOString(),
                queryParameters: { email: data.email }
            },
            message: 'No sessions found for email: ' + data.email,
            processingActivities: [],
            dataSubjectSummary: {
                totalAuditRecords: 0,
                email: data.email
            }
        },
        totalRecords: 0
    }
}];
